class Calculator {
    constructor() {
        this.result = 0;
        this.history = [];
    }

    add(a, b) {
        const result = a + b;
        this.history.push(`${a} + ${b} = ${result}`);
        return result;
    }

    subtract(a, b) {
        const result = a - b;
        this.history.push(`${a} - ${b} = ${result}`);
        return result;
    }

    multiply(a, b) {
        const result = a * b;
        this.history.push(`${a} × ${b} = ${result}`);
        return result;
    }

    divide(a, b) {
        if (b === 0) {
            throw new Error('Cannot divide by zero!');
        }
        const result = a / b;
        this.history.push(`${a} ÷ ${b} = ${result}`);
        return result;
    }

    getHistory() {
        return this.history;
    }

    clearHistory() {
        this.history = [];
    }
}

// Usage
const calc = new Calculator();
console.log(calc.add(10, 5));      // 15
console.log(calc.multiply(3, 4));  // 12
console.log(calc.getHistory());    // Shows calculation history

// DOM Manipulation Example
function createInteractiveApp() {
    const app = document.createElement('div');
    app.innerHTML = `
        <h2>Interactive Calculator</h2>
        <input type="number" id="num1" placeholder="First number">
        <select id="operation">
            <option value="add">Add (+)</option>
            <option value="subtract">Subtract (-)</option>
            <option value="multiply">Multiply (×)</option>
            <option value="divide">Divide (÷)</option>
        </select>
        <input type="number" id="num2" placeholder="Second number">
        <button onclick="calculate()">Calculate</button>
        <div id="result"></div>
        <div id="history"></div>
    `;

    document.body.appendChild(app);
}

function calculate() {
    const num1 = parseFloat(document.getElementById('num1').value);
    const num2 = parseFloat(document.getElementById('num2').value);
    const operation = document.getElementById('operation').value;
    const resultDiv = document.getElementById('result');

    if (isNaN(num1) || isNaN(num2)) {
        resultDiv.innerHTML = '<p style="color: red;">Please enter valid numbers!</p>';
        return;
    }

    try {
        let result;
        switch(operation) {
            case 'add':
                result = calc.add(num1, num2);
                break;
            case 'subtract':
                result = calc.subtract(num1, num2);
                break;
            case 'multiply':
                result = calc.multiply(num1, num2);
                break;
            case 'divide':
                result = calc.divide(num1, num2);
                break;
        }

        resultDiv.innerHTML = `<h3>Result: ${result}</h3>`;
        updateHistory();

    } catch (error) {
        resultDiv.innerHTML = `<p style="color: red;">${error.message}</p>`;
    }
}

function updateHistory() {
    const historyDiv = document.getElementById('history');
    const history = calc.getHistory();
    historyDiv.innerHTML = '<h4>History:</h4>' + 
        history.map(item => `<p>${item}</p>`).join('');
}

// Initialize the app
createInteractiveApp();
```

